targets.expr:
topk(8,
  sum by (vendor) (
    avg_over_time(erpc_upstream_score_overall{
      network="${network}",
      vendor=~"${vendor}",
      category="${category}"
    }[$__range])
  )
)

bottomk(30, sum by (network, category, finality) (
  erpc_consensus_misbehavior_detected_total{
    network=~"${network:regex}",
    project=~"${project:regex}",
    category=~"${category:regex}"
  }
))

topk(10,
  (
    sum by (network, bucket) (
      increase(erpc_network_evm_block_range_requested_total{
        network=~"${network:regex}",network!~"${ignore_network:regex}",
        category=~"${category:regex}",
        project=~"${project:regex}",
        vendor=~"${vendor:regex}",
        # bucket!~"(TIP|LATEST|FUTURE|FINALIZED)",
        category=~"(eth_getBlockByNumber|eth_getBlockByHash|eth_getLogs|eth_blockNumber|eth_getTransactionByHash|eth_getTransactionReceipt|eth_getBlockReceipts)"
      }[$__range])
    )
  )
)
